SELECT * FROM aws_athena.start_query_execution
  WHERE $body=(SELECT {
    'ClientRequestToken' : '<string>',
    'ResultConfiguration' : {
      'EncryptionConfiguration' : {
        'EncryptionOption' : 'SSE_S3 | SSE_KMS | CSE_KMS',
        'KmsKey' : '<string>'
      },
      'OutputLocation' : '<required_string>'
    },
    'QueryExecutionContext' : {
      'Database' : '<string>'
    },
    'QueryString' : '<required_string>'
  })

